<script>
  import postaukset from './postaukset.js'; 

  let postaus = [];
  let fbUrl = 'https://blogi-b5d8f-default-rtdb.europe-west1.firebasedatabase.app/';

  
//Hakee postaukset firebasesta. Kopioitu frontend kurssin materiaaleista ja s채채detty toimimaan oikealla tavalla
  const haePostaus = async() => {
		postaus = [];
		const response = await fetch(
			`${fbUrl}postaukset.json`
		);
		if (!response.ok){
			throw new Error('Dataa ei saatu');
		}
		const data = await response.json();
		for(const key in data){
			postaus.push({id: key, ...data[key]});
		}
		postaus = postaus;
    postaukset.set(postaus);
	}

haePostaus();

//Poistaa postauksen firebasesta. Kopioitu frontend kurssin materiaaleista ja s채채detty toimimaan oikealla tavalla
  const poistaPostaus = (id) => {
		fetch(`${fbUrl}postaukset/${id}.json`,{
			method: 'DELETE',
		})
		.then((response) => {
			if (!response.ok){
				throw new Error('Ei voi poistaa');
			}
			haePostaus();
		})
		.catch((err) => {
			console.log(err);
		});
	};


</script>

<div>
    <!-- Julkaisee postaukset -->
    {#each postaus as posti(posti.id)}
    <div class="postaus">
      <h1>{posti.otsikko}</h1>
      <div class="kirjoittaja">Kirjoittaja: {posti.kirjoittaja}</div>
      <div class="tekstikentta">
      <span class="teksti">{posti.postaus}</span>
    </div>
      <img class="poista" on:click={(()=> poistaPostaus(posti.id))} width="60px" src="data:image/png;base64," alt="">
    </div>
    {/each}
    <button class="paivita" on:click={(()=> haePostaus())}>P채ivit채</button>
</div>
<!-- <Footer /> -->
<style>
  .postaus{
    background-color: #edf2f4;
    position: relative;
    top:8em;
    left: 8%;
    min-width: 50%;
    min-height: 100%;
    max-width: 83%;
  }

  div .teksti{
    position: relative;
    left: 2em;
    width: 5em;
    word-wrap: break-word;
  }

  div h1, h2, h3, h4{
    text-align:center;
  }

  .paivita{
    position: fixed;
    left: 0.5em;
    top: 8em;
    z-index: 11;
    width: 8%;
    height: 8%
  }

  .postaus{
    /* background-color: beige; */
    margin: 2em 0;
    padding: 1em 0;
    box-shadow: 1px 1px 15px gray;
  }

  .poista{
    position: absolute;
    right: 1em;
    bottom: 1em;
  }


  .tekstikentta{
    width: 90%;
    margin: 2em 3%;
  }

  .kirjoittaja{
    text-align: center;
    margin-bottom: 2em;
  }
</style>